import React, { useEffect, useState } from 'react';
import Loader from 'react-loaders';
import AnimatedLetters from '../AnimatedLetters';
import './index.scss'; // Assuming the SCSS file is named experience.scss
const AnimatedLetterss = ({ letterClass, strArray, idx }) => {
  return (
    <span>
      {strArray.map((char, i) => (
        <span key={char + i} className={`${letterClass} _${i + idx}`}>
          {char}
        </span>
      ))}
    </span>
  )
}
const Experience = () => {
  const [letterClass, setLetterClass] = useState('text-animate');

  useEffect(() => {
    const timer = setTimeout(() => {
      setLetterClass('text-animate-hover');
    }, 3000);

    return () => clearTimeout(timer); // This is the cleanup function
  }, []);

  return (
    <>
      <div className="experience-container">
        <div className="text-zone">
          <h1>
            <AnimatedLetterss
              letterClass={letterClass}
              strArray={['🛠️', 'e', 'x', 'p', 'e', 'r', 'i', 'e', 'n', 'c', 'e']}
              idx={15}
            />
          </h1>
          <main className="page-content">
    <div class="card">
        <div class="content">
            <h2 class="title">Mountain View</h2>
            <p class="copy">Check out all of these gorgeous mountain trips with beautiful views of, you guessed it, the mountains</p><button class="btn">View Trips</button>
        </div>
    </div>
    <div class="card">
        <div class="content">
            <h2 class="title">To The Beach</h2>
            <p class="copy">Plan your next beach trip with these fabulous destinations</p><button class="btn">View Trips</button>
        </div>
    </div>
    <div class="card">
        <div class="content">
            <h2 class="title">Desert Destinations</h2>
            <p class="copy">It's the desert you've always dreamed of</p><button class="btn">Book Now</button>
        </div>
    </div>
    <div class="card">
        <div class="content">
            <h2 class="title">Explore The Galaxy</h2>
            <p class="copy">Seriously, straight up, just blast off into outer space today</p><button class="btn">Book Now</button>
        </div>
    </div>
    </main>
        </div>
        {/* <Loader type="pacman" /> */}
      </div>
    </>
  );
};

export default Experience;
