import React, { useState, useEffect } from 'react';
import Loader from 'react-loaders';

const MyLoader = () => {
  const [isLoading, setLoading] = useState(true);

  useEffect(() => {
    // Simulate loading data, replace with your actual loading logic
    const timer = setTimeout(() => {
      setLoading(false);
    }, 1); // 1 seconds delay

    return () => clearTimeout(timer);
  }, []);

  return (
    <>
      {isLoading && (
        <div className="loading-container" style={{ backgroundColor: 'white', position: 'fixed', top: 0, left: 0, right: 0, bottom: 0, zIndex: 9999, display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
          <Loader type="ball-pulse-sync" active />
        </div>
      )}
      <div className="content">
        {/* Your content goes here */}
      </div>
    </>
  );
};

export default MyLoader
;
