import React, { useState, useEffect } from 'react';
import Loader from 'react-loaders';
import AnimatedLetters from '../AnimatedLetters'; // Make sure to import or define this component
import './index.scss'; // Make sure to include the appropriate CSS

const Projects = () => {
  const [letterClass, setLetterClass] = useState('text-animate');
  const [activeOption, setActiveOption] = useState(0);

  const handleOptionClick = (index) => {
    setActiveOption(index);
  };

  useEffect(() => {
    const timer = setTimeout(() => {
      setLetterClass('text-animate-hover');
    }, 3000);
    return () => clearTimeout(timer);
  }, []);

  const imageUrls = [
    // Add URLs to the images you want to display
  ];

  return (
    <>
      <div className="container about-page">
        <div className="text-zone">
          <h1>
            <AnimatedLetters
              letterClass={letterClass}
              strArray={['ðŸ› ï¸', 'p', 'r', 'o', 'j', 'e', 'c', 't', 's']}
              idx={15}
            />
          </h1>
          <div className="options">
            {imageUrls.map((url, index) => (
              <div
                className={`option ${activeOption === index ? 'active' : ''}`}
                style={{ '--optionBackground': `url(${url})` }}
                onClick={() => handleOptionClick(index)}
              >
                <div className="shadow"></div>
                <div className="label">
                  <div className="icon">
                    <i className="fas fa-sun"></i> {/* Change the icon as needed */}
                  </div>
                  <div className="info">
                    <div className="main">Title</div>
                    <div className="sub">Subtitle</div>
                  </div>
                </div>
              </div>
            ))}
          </div>
          <a href="http://victorofvalencia-blog.tumblr.com" target="_blank" rel="noreferrer" className="credit">
            Photos from Victor of Valencia on tumblr
          </a>
        </div>
      </div>
      <Loader type="pacman" />
    </>
  );
};

export default Projects;
